<script>
    document.querySelector('.entrar').addEventListener('mouseover', function() {
        document.querySelectorAll('g').forEach(function(g) {
            g.classList.add('hovered');
        });
    });
    document.querySelector('.entrar').addEventListener('mouseout', function() {
        document.querySelectorAll('g').forEach(function(g) {
            g.classList.remove('hovered');
        });
    });
</script>

